CREATE TABLE USER_INFO (user_name varchar(50), password varchar(160), account_non_expired bit, account_non_locked bit, credentials_non_expired bit, enabled bit, primary key (user_name));
CREATE TABLE COURIER_MASTER(id varchar(36), comp_name varchar(100), cnt_prsn varchar(100), mobile varchar(15), street varchar(100), city varchar(60), state varchar(60), pin_code varchar(10), primary key (id));
CREATE TABLE EMPLOYEE_MASTER(id varchar(36), name varchar(100), designation varchar(50), mobile varchar(15), email varchar(100), primary key (id));
CREATE TABLE CUSTOMER_MASTER(client_id varchar(20), ucc_id varchar(20), name varchar(100), mobile varchar(15), email varchar(100), pan_num varchar(20), dob date, acc_num varchar(36), bank varchar(100), ifsc varchar(20), acct_type varchar(50), act_date dateTime, balance decimal(20,2), dec_date dateTime, corr_steet varchar(100), corr_city varchar(50), corr_state varchar(50), corr_pin varchar(10), per_steet varchar(100), per_city varchar(50), per_state varchar(50), per_pin varchar(10), primary key (client_id));
CREATE TABLE MEETING_MASTER(id varchar(36), meeting_time datetime, client_type varchar(20), client_id varchar(36), meeting_notes varchar(300), primary key (id));
CREATE TABLE NON_CLIENT_MASTER (id varchar(36), name varchar(100), mobile varchar(15), email varchar(100), primary key (id));
CREATE TABLE STOCK_INFO (isc_num varchar(36), name varchar(100), symbol varchar(15), close_date Date, pre_cl_val decimal(20,2), primary key (isc_num));
CREATE TABLE TXN_INFO(id varchar(36), client_id varchar(36), txn_time datetime, isc_num varchar(36), quantity Decimal(10,2), avg_price decimal(20,2), primary key (id));